import React, { Component } from 'react';
import './App.css';
import ApiTest from './ApiTest';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemon: []
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/evolution-chain/3/')
          .then(response => response.json())
          .then(pokemonObj => this.setState({ pokemon: pokemonObj }));
  }

  render() {
    const obj = this.state.pokemon;
    return (
      <div>
        {obj}
      </div>
    )
  }
}

export default App;
