import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemon: [],
          initialLoad: true
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            console.log('fetch', data.results);
            return this.setState({ pokemon: data.results, initialLoad: false })
          })
  }

  render() {
    const pokemonList = this.state.pokemon;
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox />
          <CardList pokemonList={pokemonList}/>
      </div>
    )
  }
}

export default App;
