import React from 'react';


const Card = (props) => {
    // const response = await fetch('https://pokeapi.co/api/v2/pokemon/1');
    // const data = await response.json();
    // const pokemonName = data.name;
    // console.log('card', pokemonName, typeof(pokemonName));
    const name = fetch('https://pokeapi.co/api/v2/pokemon/1')
        .then(response => response.json()).then(data => data.name);
    return (
        <div className='tc bg-light-yellow dib br3 pa3 ma2 grow bw2 shadow-5'>
            <img alt="pokemon" src="https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/1.svg" />
            <h2>{name}</h2>
            <p>Email</p>
        </div>
    );

}


export default Card;
