import React, { Component } from 'react';
import Card from './Card';

class CardList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            pokedex: [],
            pokemonList: props.pokemonList
        }
    }

    componentDidMount() {
        const pokemonUrls = this.state.pokemonList.map(pokemon => pokemon.url);
        console.log('urls', pokemonUrls);
        pokemonUrls.map((url => fetch(`${url}`)).then(response => response.json())
        .then(data => {
            console.log('pokedex', data)
            this.setState({ pokedex: data })
        }))
    }

    render() {
        console.log('pokelist', this.state.pokemonList);
        return (
            <div>
                {
                    this.state.pokemonList.map(pokemon => {
                        return(
                            <Card key={pokemon.name} name={pokemon.name} url={pokemon.url}/>
                        );
                    })
                }
            </div>
        );
    }

}

