      // .then(data => {
      //     console.log('pokedex', data)
      //     this.setSdtate({ pokedex: data })
      // }))
  }

  render() {
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox />
          <CardList pokemonList={this.state.pokemon}/>
      </div>
    );
  }
}

export default App;
