import React, { Component } from 'react';
import './App.css';
import SearchBox from './SearchBox';
import PokemonLists from './PokemonLists';
import ErrorBoundary from './ErrorBoundary';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemonDisplayed: [],
          displayEvolution: false
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            const pokemonUrls = data.results.map(item => item.url);
            pokemonUrls.forEach(item => {
              fetch(item).then(resp => resp.json()).then(data => {
              this.setState({ pokemonDisplayed: data});
              }).catch(err => {
              console.log("Error. Please try again.")});


            })
          })
  }

  onMouseClick = (event) => {
    if (!this.state.displayEvolution){
      const clickId = parseInt(event.target.id)
      const pokemonClicked = this.state.pokemonDisplayed.filter(item => item.id === clickId);
      this.setState({ pokemonDisplayed: pokemonClicked, displayEvolution: true });
    }
  }

  onSearch = (event) => {
    if(event.keyCode === 13 || event.type === "click"){
          const enteredName = document.querySelector("#searchbox").value.toLowerCase().trim()
    fetch(`https://pokeapi.co/api/v2/pokemon/${enteredName}/`)
      .then(response => response.json())
      .then(data => {
        const dataArray = [data]
        this.setState({ pokemonDisplayed: dataArray, displayEvolution: true});
      }).catch(err => {
        console.log("Error. Please try again.")
      })
    }
  }

  render() {
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox onSearch={this.onSearch}/>
          <ErrorBoundary>
              <PokemonLists 
              displayEvolution={this.state.displayEvolution}
              pokemonList={this.state.pokemonDisplayed}
              onMouseClick={this.onMouseClick}/>
          </ErrorBoundary>
      </div>
    );
  }
}

export default App;
