import React from 'react';
import Card from './Card';

const getPokemonEvoChain = (pokemon) => {
    fetch(`https://pokeapi.co/api/v2/evolution-chain/${pokemon[0].id}/`)
        .then(response => response.json())
        .then(data => {
            console.log("FETCH", data)
        })
}

const EvolutionChain = ({pokemonList, onMouseClick}) => {
    const pokemon = getPokemonEvoChain(pokemonList);
    console.log("evolution", pokemonList);
    return (
        <div>
            {
                pokemonList.map(pokemon => {
                    return(
                        <Card key={pokemon.id} name={pokemon.name} id={pokemon.id} onMouseClick={onMouseClick}/>
                    );
                })
            }
        </div>
    );
};

export default EvolutionChain;
