import React, { Component } from 'react';
import Card from './Card';

class CardList extends Component {
    constructor({pokemonList}) {
        super({pokemonList})
        this.state = {
            pokedex: [],
            pokemonList: pokemonList
        }
    }

    componentDidMount() {
        const pokemonUrls = this.state.pokemonList.map(pokemon => pokemon.url);
        pokemonUrls.map((url => fetch(url)).then(response => response.json())
        .then(data => {
            console.log('pokedex', data)
            return this.setState({ pokedex: data })
        }))
    }

    render() {
        return (
            <div>
                {
                    this.state.pokemonList.map(pokemon => {
                        return(
                            <Card key={pokemon.name} name={pokemon.name} url={pokemon.url}/>
                        );
                    })
                }
            </div>
        );
    }

}

