import React, { Component } from 'react';
import './App.css';
import SearchBox from './SearchBox';
import PokemonLists from './PokemonLists';
import ErrorBoundary from './ErrorBoundary';
import EvolutionChain from './EvolutionChain';
import CardList from './CardList';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemonDisplayed: [],
          displayEvolution: false
      }
  }

  //Fetch first 20 pokemon to display
  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            const pokemonUrls = data.results.map(item => item.url);
            const pokemonPromises = pokemonUrls.map(item => {
              return fetch(item).then(resp => resp.json())
              })
            Promise.all(pokemonPromises)
              .then(responses => {this.setState({ pokemonDisplayed: responses});})
              .catch(err => console.log("Error. Please try again."))
            })
            .catch(err => console.log("Error. Please try again."))
  }

  //Identify clicked pokemon to update pokemon displayed
  onItemClick = (event) => {
      if (!this.state.displayEvolution){
          const clickId = parseInt(event.target.id)
          const pokemonClicked = this.state.pokemonDisplayed.filter(item => item.id === clickId);
          this.setState({ pokemonDisplayed: pokemonClicked, displayEvolution: true });
      }
  }

  //Fetch pokemon data entered into searchbox to update pokemon displayed
  onSearch = (event) => {
      if(event.keyCode === 13 || event.type === "click"){
          const enteredName = document.querySelector("#searchbox").value.toLowerCase().trim()
          fetch(`https://pokeapi.co/api/v2/pokemon/${enteredName}/`)
              .then(response => response.json())
              .then(data => {
                  console.log(data);
                  this.setState({ pokemonDisplayed: [data], displayEvolution: true});
              }).catch(err => {
                  console.log("Error. Please try again.")
              })
      }
  }

  pokemonList = () => {
    if (this.state.displayEvolution) {
      return <EvolutionChain pokemonList={this.state.pokemonList} onItemClick={this.onItemClick}/>
    } else {
      return <CardList pokemonList={this.state.pokemonList} onItemClick={this.onItemClick}/>
    }
  }

  //Render
  render() {
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox onSearch={this.onSearch}/>
          <ErrorBoundary>
            {pokemonList()}
          </ErrorBoundary>
      </div>
    );
  }
}

export default App;
