        }
    }
    componentDidMount({pokemonList}) {
        const pokemonArray = pokemonList.map
        fetch('https://pokeapi.co/api/v2/pokemon/')
            .then(response => response.json())
            .then(data => {
              console.log('pokedex', data.results);
              return this.setState({ pokemon: data.results })
