import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemonObjects: [],
          newObj: []
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            // console.log('fetch', data.results);
            const smallObj = data.results;
            let brandObj = [];
            smallObj.map(item => {
              return fetch(item.url)
                .then(resp => resp.json())
                .then(data => {
                  // console.log("DATA", data);
                  // this.setState({ pokemonObjects: data })
                  brandObj.push(data)})
            })
            console.log("BRAND", brandObj)
            this.setState({ newObj: brandObj })
            // this.setState({ pokemon: data.results })
          })

      // const pokemonUrls = this.state.pokemon.map(pokemon => pokemon.url);
      // console.log('urls', pokemonUrls);
      // pokemonUrls.map(url => fetch(url).then(response => response.json())
      // .then(data => {
      //     console.log('pokedex', data)
      //     this.setSdtate({ pokedex: data })
      // }))
  }

  render() {
    console.log("PPBJ", this.state.pokemonObjects)
    console.log("NEWOB", this.state.newObj)
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox />
          <CardList pokemonList={this.state.newObj}/>
      </div>
    )
  }
}

export default App;
