import React, { Component } from 'react';
import './App.css';
import SearchBox from './SearchBox';
import PokemonLists from './PokemonLists';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemonDisplayed: [],
          displayEvolution: false
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            const initialPokemon = data.results;
            const pokemonUrls = initialPokemon.map(item => item.url);
            const pokemonPromises = pokemonUrls.map(item => {
              return fetch(item).then(resp => resp.json()).then(data => data)
              })

            Promise.all(pokemonPromises).then(responses => {
              const pokemonArray = responses.map(resp => resp)
              this.setState({ pokemonDisplayed: pokemonArray});
            })
            })
  }

  onMouseClick = (event) => {
    if (!this.state.displayEvolution){
      const clickId = Number(event.target.id)
      const pokemonClicked = this.state.pokemonDisplayed.filter(item => item.id === clickId);
      this.setState({ pokemonDisplayed: pokemonClicked, displayEvolution: true });
    }
  }

  onSearch = () => {
    const enteredName = document.querySelector("#searchbox").value.toLowerCase().trim()
    fetch(`https://pokeapi.co/api/v2/pokemon/${enteredName}/`)
      .then(response => response.json())
      .then(data => {
        const dataArray = [data]
        this.setState({ pokemonDisplayed: dataArray, displayEvolution: true});
      }).catch((err) => {
        alert("Unable to find Pokemon. Please try again.")
      })
  }

  // onKeyDown = (event) => {
  //   if (event.keyCode === 13) {
  //     const enteredName = document.querySelector("#searchbox").value.toLowerCase().trim()
  //     fetch(`https://pokeapi.co/api/v2/pokemon/${enteredName}/`)
  //       .then(response => response.json())
  //       .then(data => {
  //         const dataArray = [data]
  //         this.setState({ pokemonDisplayed: dataArray, displayEvolution: true});
  //       }).catch((err) => {
  //         alert("Unable to find Pokemon. Please try again.")
  //       })    }
  // }

  render() {
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox onSearch={this.onSearch} onKeyDown={this.onKeyDown}/>
          <PokemonLists 
            displayEvolution={this.state.displayEvolution}
            pokemonList={this.state.pokemonDisplayed}
            onMouseClick={this.onMouseClick}/>
      </div>
    );
  }
}

export default App;
