import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemonDisplayed: [],
          pokemonClickedId: []
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            const initialPokemon = data.results;
            const pokemonUrls = initialPokemon.map(item => item.url);
            const pokemonPromises = pokemonUrls.map(item => {
              return fetch(item).then(resp => resp.json()).then(data => data)
              })

            Promise.all(pokemonPromises).then(responses => {
              const pokemonArray = responses.map(resp => resp)
              this.setState({ pokemonDisplayed: pokemonArray});
            })
            })
  }

  onMouseClick = (event) => {
    const clickId = Number(event.target.id)
    const pokemonClicked = this.state.pokemonDisplayed.filter(item => item.id === clickId);
    this.setState({ pokemonDisplayed: pokemonClicked });
}

  onSearch = () => {
    const enteredName = document.querySelector("#searchbox").value;
    fetch(`https://pokeapi.co/api/v2/pokemon/${enteredName}/`)
      .then(response => response.json())
      .then(data => {
        const dataArray = [data]
        this.setState({ pokemonDisplayed: dataArray});
      }).catch((err) => {
        const invalidPokemon = "Unable to find Pokemon. Please try again."
        alert("UNABLE TO FIND POKEMON", err)
      })
  }

  render() {
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox onSearch={this.onSearch}/>
          <CardList pokemonList={this.state.pokemonDisplayed} onMouseClick={this.onMouseClick}/>
      </div>
    );
  }
}

export default App;
