import React from 'react';
import Card from './Card';

const getPokemonEvoChainUrl = (pokemon) => {
    const pokemonEvoChain = [];    
    fetch(pokemon[0].species.url)
        .then(response => response.json())
        .then(data => {
            fetch(data.evolution_chain.url)
                .then(resp => resp.json())
                .then(data1 => {
                    console.log("DATA!!", data1);
                    function chainDepth(evo) {
                        const evolutionPokemon = {};

                        evolutionPokemon['name'] = evo.species.name;
                        const url = evo.species.url
                        const pokemonId = url.slice(42, -1)
                        evolutionPokemon.url = pokemonId
                        pokemonEvoChain.push(evolutionPokemon)

                        let evoChain = evo;
                        let nextChain = evoChain.evolves_to;
                        if (nextChain.length > 0) {
                            chainDepth(nextChain[0]);
                        }
                    }
                    chainDepth(data1.chain)

                    // let chain = data1.chain.evolves_to;

                    // console.log("DATA", data1);
                    // console.log("chain!!!", data1.chain.evolves_to[0]);
                    // console.log("chainLength", chain.length);

                    // for (let i = 0; i < 3; i++) {
                    //     const evolutionPokemon = {};
                    //     evolutionPokemon['name'] = data1.chain.species.name;
                    //     evolutionPokemon.id = data1.id
                    //     pokemonEvoChain.push(evolutionPokemon)
                    //     console.log("data1",data1)
                    //     console.log("EVOPOke", evolutionPokemon)
                    //     chain.concat([0].evolves_to)
                    // }

                })
        })
    console.log("TESTARRAY", pokemonEvoChain);
    // return pokemonUrl;
}

const EvolutionChain = ({pokemonList, onMouseClick}) => {
    const pokemonEvolutionList = getPokemonEvoChainUrl(pokemonList);
    // console.log("POKEMON", pokemon)
    // const evolutionChain = getPokemonEvoChain(pokemon);
    // console.log("EVOCHAIN", evolutionChain)
    return (
        <div>
            {
                pokemonList.map(pokemon => {
                    return(
                        <Card key={pokemon.id} name={pokemon.name} id={pokemon.id} onMouseClick={onMouseClick}/>
                    );
                })
            }
        </div>
    );
};

export default EvolutionChain;
