import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemonDisplayed: [],
          pokemonClickedId: []
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            const initialPokemon = data.results;
            const pokemonUrls = initialPokemon.map(item => item.url);
            const pokemonPromises = pokemonUrls.map(item => {
              return fetch(item).then(resp => resp.json()).then(data => data)
              })

            Promise.all(pokemonPromises).then(responses => {
              const pokemonArray = responses.map(resp => resp)
              this.setState({ pokemonDisplayed: pokemonArray});
            })
            })
  }

  onMouseClick = (event) => {
    this.setState({ pokemonClickedId: Number(event.target.id) });
}

  render() {
    const clickId = this.state.pokemonClickedId
    const filteredPokemon = this.state.pokemonDisplayed.filter(item => item.id === clickId);

    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox />
          <CardList pokemonList={filteredPokemon} onMouseClick={this.onMouseClick}/>
      </div>
    );
  }
}

export default App;
