import React, { Component } from 'react';

class ApiTest extends Component {
    constructor() {
        super()
        this.state = {
            pokemon: []
        }
    }

    componentDidMount() {
        fetch('https://pokeapi.co/api/v2/evolution-chain/3/')
            .then(response => response.json())
            .then(pokemonObj => this.setState({ pokemon: pokemonObj }));
    }

    render() {
        return (
            <div>
                {
                    this.state.pokemon.map(item => (
                    {item}))
                }
            </div>
        );
    }
}

export default ApiTest;
