import React from 'react';


const Card = ({name, id, onMouseClick}) => {
    return (
        <div className='mw-100 tc bg-light-yellow dib br3 pa3 ma2 grow bw2 shadow-5'
            onClick={onMouseClick}>
            <img id={id}alt="pokemon" src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${id}.svg`} />
            <h2>{name}</h2>
        </div>
    );

}


export default Card;
