import React, { Component } from 'react';
import './App.css';
import CardList from './CardList';
import SearchBox from './SearchBox';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemon: [],
          pokedex: [],
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/pokemon/')
          .then(response => response.json())
          .then(data => {
            console.log('fetch', data.results);
            return this.setState({ pokemon: data.results })
          })

      const pokemonUrls = this.state.pokemon.map(pokemon => pokemon.url);
      console.log('urls', pokemonUrls);
      pokemonUrls.map(url => fetch(url).then(response => response.json())
      .then(data => {
          console.log('pokedex', data)
          this.setState({ pokedex: data })
      }))
  }

  render() {
    return (
      <div className='tc'>
        <h1>Pokemon Evolution</h1>
          <SearchBox />
          <CardList pokemonList={this.state.pokemon}/>
      </div>
    )
  }
}

export default App;
