import React from 'react';
import Card from './Card';



class EvolutionChain extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            pokemonList: props.pokemonList,
            onMouseClick: props.onMouseClick,
            evolutionList: []
        }
    };

    getPokemonEvoChainUrl = (pokemon) => {
        let pokemonEvoChain = [];    
        fetch(pokemon[0].species.url)
            .then(response => response.json())
            .then(data => {
                fetch(data.evolution_chain.url)
                    .then(resp => resp.json())
                    .then(data1 => {
                        function chainDepth(evo) {
                            const evolutionPokemon = {};

                            evolutionPokemon['name'] = evo.species.name;
                            const url = evo.species.url
                            const pokemonId = url.slice(42, -1)
                            evolutionPokemon.id = Number(pokemonId)
                            pokemonEvoChain.push(evolutionPokemon)

                            let evoChain = evo;
                            let nextChain = evoChain.evolves_to;
                            if (nextChain.length > 0) {
                                chainDepth(nextChain[0]);
                            }
                        }
                        chainDepth(data1.chain)
                    })
            })
        console.log("TESTARRAY", pokemonEvoChain);
        this.setState({evolutionList: pokemonEvoChain});
    }

    render() {
        // const pokemonEvolutionList = this.getPokemonEvoChainUrl(this.state.pokemonList);
        // console.log("POKEMON", pokemonEvolutionList.length)
        // console.log("POKEMON0", pokemonEvolutionList[0])
        // console.log("POKEMONAME", pokemonEvolutionList.name)
        // const newArray = pokemonEvolutionList.map(item => {
        //     console.log("ITEMSSS", item);
        //     return item.name;
        // })
        // console.log(newArray);
        return (
            <div>
                {
                    this.state.evolutionList.map(pokemon => {
                        console.log("MAPINFO", pokemon)
                        return(
                            <Card key={pokemon.id} name={pokemon.name} id={pokemon.id} onMouseClick={this.onMouseClick}/>
                        );
                    })
                }
            </div>
        );
    }

}

export default EvolutionChain;
