import React, { Component } from 'react';
import './App.css';
import Card from './Card';

class App extends Component {
  constructor() {
      super()
      this.state = {
          pokemon: []
      }
  }

  componentDidMount() {
      fetch('https://pokeapi.co/api/v2/evolution-chain/3/')
          .then(response => response.json())
          .then(data => {
            console.log('fetch', data.chain.species.name);
            return this.setState({ pokemon: data })
          })
  }

  render() {
    const { pokemon } = this.state;
    return (
      <div>
        <Card />
      </div>
    )
  }
}

export default App;
